import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";

import Table from "../layout/Table";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      search: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    this.setState({[event.target.search] : event.target.value})
  }

  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };

render() {
    document.body.style = 'background: white;';
    const { user } = this.props.auth;
    console.log(user);
return (
      <div style={{ height: "75vh" }} className="container valign-wrapper">
        <div className="row">
          <div className="col s12 center-align">
            <h4>
              <b>Hey there,</b> {user.name.split(" ")[0]}
              <p className="flow-text grey-text text-darken-1">
                You are logged into a full-stack{" "}
                <span style={{ fontFamily: "monospace" }}>MERN</span> app üëè
              </p>
              <b>Full info</b> {user.tenant.join(', ')}
              <p className="flow-text grey-text text-darken-1">

              </p>
            </h4>
            <div class="input-field inline" onSubmit={this.handleSubmit}>
              <input value={this.state.value} onChange={this.handleChange}
                id="search_bar" type="text"/>
              <label for="search_bar">Search...</label>
              <button class="waves-effect waves-light btn-small" type="submit" name="action">Submit
              </button>
            </div>
            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              className="btn btn-large waves-effect waves-light hoverable blue accent-3"
            >
              Logout
            </button>
          </div>
        </div>
        <div>
          <Table tenant={user.tenant} search={this.state.value}/>
        </div>
      </div>

    );
  }
}
Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth
});
export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
